import styled from "styled-components";

export const Container = styled.div`
    h1 {
        color: var(--white);
        font-weight: 500;
    }

    h2 {
        color: var(--light-gray);
        font-weight: 400;
    }

    button {
        background: var(--blue);
        color: var(--white);
        height: 2rem;
    }
`;
