import styled from "styled-components";

export const Global = styled.div`
    :root {
        --gray: #616161;
        --light-gray: #bfbfc0;
        --cloud-gray: #eceff1;
        --black: #000000;
        --white: #fff;
        --blue: #322af1;
    }

    * {
        background: var(--black);
    }

    body {
        background: var(--black);
    }
`;
